mapper.update:
UPDATE application
SET usr_gpa = #{userGpa}, award = #{award}, reason = #{reason}, can_adjust = #{canAdjust}
WHERE usr_id = #{userID} AND scholar_year = #{year} AND scholar_name = #{scholarName}

UPDATE application
SET usr_score = #{score}
WHERE usr_id = #{userID} AND scholar_year = #{year} AND scholar_name = #{scholarName}

UPDATE application
SET status = #{status}
WHERE usr_id = #{userID} AND scholar_year = #{year} AND scholar_name = #{scholarName}
